package ClasesOperadores;

public class Ejercicio2 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int var1 =9;
		int var2 =7;
		System.out.println("la Suma de "+var1+" y "+var2+" es: "+(var1+var2));
		System.out.println("la Resta de "+var1+" y "+var2+" es: "+(var1-var2));
		System.out.println("la Multiplicacion de "+var1+" y "+var2+" es: "+(var1*var2));
		System.out.println("la Divicion de "+var1+" y "+var2+" es: "+var1/var2);
	}

}
